$.fn.serializeObject = function() {
    var o = {};
    var a = this.serializeArray();
    //var notChkO=$('input[type=checkbox]:not(:checked)', this);
    $.each(a, function() {
        if (o[this.name]) {
            if (!o[this.name].push) {
                o[this.name] = [ o[this.name] ];
            }
            o[this.name].push(this.value || '');
        } else {
            o[this.name] = this.value || '';
        }
    });
    if(o['status']){
        o['status'] = '0';
    }
    return o;
};

$.extend({
    postJson: function (url, data, callback) {
        $.ajax({
            url: url,
            type: "POST",
            data: data,
            contentType: "application/json",
            success: function (result) {
                if (callback != null)
                    callback(result);
            }
        });
    }
});

$.extend({
    postFormJson: function (formId, callback) {
        var form = $("#" + formId);
        $.postJson(form.attr("action"), JSON.stringify(form.serializeObject()), callback);
    }
});
